def on_on_overlap(sprite, otherSprite):
    otherSprite.set_kind(SpriteKind.Dead)
    otherSprite.set_flag(SpriteFlag.GHOST, True)
    characterAnimations.set_character_animations_enabled(otherSprite, False)
    animation.run_image_animation(otherSprite,
        [img("""
            . . . . . f f f . . . . 
                    . . . . f 9 9 6 f . . . 
                    . . . f e e 9 9 6 f . . 
                    . . . f f f e e 6 f f . 
                    . . . f 9 9 f f e f e f 
                    . . . . f 9 6 6 f e f . 
                    . . 3 3 f f f f f f . . 
                    . f f f 9 9 6 f f d d . 
                    f e e f 6 6 f . . d d . 
                    . . . . f f 4 f . . . . 
                    . . . . . f 4 4 f . . . 
                    . . . . . . f 4 f . . .
        """)],
        350,
        False)
    if ninjiSprite.x < otherSprite.x:
        otherSprite.vx = 50
    elif ninjiSprite.x > otherSprite.x:
        otherSprite.vx = -50
    otherSprite.vy = -150
    sprite.destroy()
    scene.camera_shake(2, 500)
    
    def on_after():
        otherSprite.destroy()
    timer.after(3000, on_after)
    
sprites.on_overlap(SpriteKind.projectile, SpriteKind.Runner, on_on_overlap)

