def on_on_overlap(sprite, otherSprite):
    gameOver()
    scene.camera_shake(4, 500)
    sprite.set_flag(SpriteFlag.GHOST_THROUGH_SPRITES, True)
    
    def on_after():
        sprite.set_flag(SpriteFlag.GHOST_THROUGH_SPRITES, False)
    timer.after(1500, on_after)
    
sprites.on_overlap(SpriteKind.Hitbox, SpriteKind.BossProjectile, on_on_overlap)

