sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Yellow, function (sprite, otherSprite) {
    otherSprite.setKind(SpriteKind.Dead)
    otherSprite.setFlag(SpriteFlag.Ghost, true)
    characterAnimations.setCharacterAnimationsEnabled(otherSprite, false)
    animation.runImageAnimation(
    otherSprite,
    [img`
        . . . . . f f f . . . . 
        . . . . f 5 5 4 f . . . 
        . . . f e e 5 5 4 f . . 
        . . . f f f e e 4 f f . 
        . . . f 5 5 f f e f e f 
        . . . . f 5 4 4 f e f . 
        . . 3 3 f f f f f f . . 
        . f f f 5 5 4 f f d d . 
        f e e f 4 4 f . . d d . 
        . . . . f f 4 f . . . . 
        . . . . . f 4 4 f . . . 
        . . . . . . f 4 f . . . 
        `],
    350,
    false
    )
    if (ninjiSprite.x < otherSprite.x) {
        otherSprite.vx = 50
    } else if (ninjiSprite.x > otherSprite.x) {
        otherSprite.vx = -50
    }
    otherSprite.vy = -150
    sprite.destroy()
    scene.cameraShake(2, 500)
    timer.after(3000, function () {
        otherSprite.destroy()
    })
})
