sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Runner, function (sprite, otherSprite) {
    otherSprite.setKind(SpriteKind.Dead)
    otherSprite.setFlag(SpriteFlag.Ghost, true)
    characterAnimations.setCharacterAnimationsEnabled(otherSprite, false)
    animation.runImageAnimation(
    otherSprite,
    [img`
        . . . . . f f f . . . . 
        . . . . f 9 9 6 f . . . 
        . . . f e e 9 9 6 f . . 
        . . . f f f e e 6 f f . 
        . . . f 9 9 f f e f e f 
        . . . . f 9 6 6 f e f . 
        . . 3 3 f f f f f f . . 
        . f f f 9 9 6 f f d d . 
        f e e f 6 6 f . . d d . 
        . . . . f f 4 f . . . . 
        . . . . . f 4 4 f . . . 
        . . . . . . f 4 f . . . 
        `],
    350,
    false
    )
    if (ninjiSprite.x < otherSprite.x) {
        otherSprite.vx = 50
    } else if (ninjiSprite.x > otherSprite.x) {
        otherSprite.vx = -50
    }
    otherSprite.vy = -150
    sprite.destroy()
    scene.cameraShake(2, 500)
    score += 10
    timer.after(3000, function () {
        otherSprite.destroy()
    })
})
