sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Sneaker, function (sprite, otherSprite) {
    otherSprite.setKind(SpriteKind.Dead)
    otherSprite.setFlag(SpriteFlag.Ghost, true)
    characterAnimations.setCharacterAnimationsEnabled(otherSprite, false)
    animation.runImageAnimation(
    otherSprite,
    [img`
        . . . . . . . . . . . . 
        . . . . . . 6 7 . . . . 
        . . 6 7 7 7 7 . . . . . 
        . . . 8 6 8 8 6 . . . . 
        . 8 6 8 8 6 6 8 7 6 . . 
        . . 8 6 6 8 8 6 8 . . . 
        6 7 7 8 7 6 6 7 8 6 8 . 
        . f 6 6 6 7 7 6 6 8 . . 
        . f 8 6 8 6 6 8 8 7 7 6 
        . . . f 6 8 8 6 6 6 . . 
        . . . . 8 6 6 8 6 8 . . 
        . . . . . . f 4 f . . . 
        `],
    50,
    false
    )
    if (ninjiSprite.x < otherSprite.x) {
        otherSprite.vx = 50
    } else if (ninjiSprite.x > otherSprite.x) {
        otherSprite.vx = -50
    }
    otherSprite.vy = -150
    sprite.destroy()
    scene.cameraShake(2, 500)
    score += 10
    timer.after(3000, function () {
        otherSprite.destroy()
    })
})
sprites.onOverlap(SpriteKind.Hitbox, SpriteKind.Boss, function (sprite, otherSprite) {
    gameOver()
    scene.cameraShake(4, 500)
    sprite.setFlag(SpriteFlag.GhostThroughSprites, true)
    timer.after(1500, function () {
        sprite.setFlag(SpriteFlag.GhostThroughSprites, false)
    })
})
scene.onOverlapTile(SpriteKind.Boss, assets.tile`myTile29`, function (sprite, location) {
	
})
scene.onOverlapTile(SpriteKind.Hitbox, assets.tile`myTile45`, function (sprite, location) {
    tiles.setTileAt(location, assets.tile`transparency8`)
    if (gameRunning) {
        lives += 1
    }
})
