scene.onHitWall(SpriteKind.Boss, function (sprite, location) {
    if (level == 4) {
        if (sprite.isHittingTile(CollisionDirection.Left)) {
            bossRunCount += 0.5
            sprite.vx = 80
        }
        if (sprite.isHittingTile(CollisionDirection.Right)) {
            sprite.vx = -80
            bossRunCount += 0.5
        }
    } else if (level == 11) {
        if (sprite.isHittingTile(CollisionDirection.Left)) {
            bossRunCount += 0.5
            sprite.vx = 96
        }
        if (sprite.isHittingTile(CollisionDirection.Right)) {
            sprite.vx = -96
            bossRunCount += 0.5
        }
    }
})
